mapper.select:
select o.conf_order_id as confOrderId , o.user_id as userId, u.user_name as userName,o.conf_id as confId, date_format(o.create_time,'%Y-%m-%d %h:%i') createTime,  date_format(o.start_time,'%Y-%m-%d %h:%i') startTime, date_format(o.end_time,'%Y-%m-%d %h:%i') endTime, o.conf_title as confTitle, o.status from sys_conf_order o ,sys_user  u where o.user_id=u.user_id  and o.status=0

     and o.conf_id = #{confId}
     and o.start_time = #{startTime}
     and o.end_time = #{endTime}
     and o.conf_title = #{confTitle}
     and o.status = #{status}

mapper.update:
update sys_conf_order
user_id = #{userId}, conf_id = #{confId}, create_time = #{createTime}, start_time = #{startTime}, end_time = #{endTime}, conf_title = #{confTitle}, status = #{status},
where conf_order_id = #{confOrderId}
